package de.shiyar.utilities.poeditor.db.entities;import lombok.*;import javax.persistence.*;import java.io.Serializable;/** * @author daoud * on Aug, 2019 */@Entity@Table(name = "messages_source")@NoArgsConstructor@Setter@Getter@ToString@Builder@AllArgsConstructorpublic class MessagesEntity implements Serializable {    private static final long serialVersionUID = 1L;    @Id    @Column(name = "id")    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "code")    private String code;    @Column(name = "local")    private String local;    @Column(name = "message")    private String message;}