package de.shiyar.utilities.poeditor.db.repositories;import de.shiyar.utilities.poeditor.db.entities.MessagesEntity;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.PagingAndSortingRepository;import org.springframework.stereotype.Repository;import javax.transaction.Transactional;/** * @author daoud * on Aug, 2019 */@Repositorypublic interface MessagesRepository extends PagingAndSortingRepository<MessagesEntity, Long> {    @Modifying    @Query(value = "INSERT INTO messages_source (code, local, message) VALUES(?1, ?2, ?3) ON DUPLICATE KEY UPDATE message=?3", nativeQuery = true)    @Transactional    void insertUpdate(String code, String local, String message);}